INSERT INTO `sys_log` VALUES (690, '编辑动态', '{\"id\":17,\"软删除\":0,\"名称\":\"开始考研，太容易急于求成了怎么办？\",\"内容\":\"<p style=\\\"text-align: start;\\\"><span style=\\\"color: rgb(18, 18, 18); font-size: 15px;\\\"> &nbsp; &nbsp;这世界上最怕的事情，就是知道该怎么做，但是就一直处于焦虑的状态，不能继续做下去！</span></p><p style=\\\"text-align: start;\\\"><span style=\\\"color: rgb(18, 18, 18); font-size: 15px;\\\"> &nbsp; &nbsp;我是今年准备考研的考研人，我知道考研这件事情，必须将每个知识点都应该完全掌握，又或者说将自己会的东西全部掌握，考试遇见不会出错才是上策，应该慢慢的学！</span></p><p style=\\\"text-align: start;\\\"><span style=\\\"color: rgb(18, 18, 18); font-size: 15px;\\\"> &nbsp; &nbsp;但是呢，我看见了大量的要学习的内容，看见同学的进度比我快，我就很慌，虽然我知道有可能他学的也不是很扎实！但是就是心里很难受！</span></p><p style=\\\"text-align: start;\\\"><span style=\\\"color: rgb(18, 18, 18); font-size: 15px;\\\"> &nbsp; &nbsp;我的想法是把网课中的每个知识点都完全学会再开始下一个内容，但是呢，这样花费的时间就会增加，到最后就会和同学之间在进度上岔开很多差距。我也懂得慢就是快，快就是慢的道理，但就是做不到，就是很焦虑怎么办？</span></p><p style=\\\"text-align: start;\\\"><br></p>\",\"图片\":\"http://localhost:9090/file/download/2fc1d56dec1c4dbc8a991db4133fe445.jpg\",\"用户id\":17,\"时间\":\"2023-03-25 11:59:02\",\"创建时间\":1679716743000,\"更新时间\":1679716743000,\"descr\":\"开始考研，就遇见了一个很大问题，太容易急于求成了怎么办？\",\"view\":22,\"user\":{\"id\":17,\"用户名\":\"vbbb\",\"密码\":\"$2a$10$sHX8UzVB2s5abnkw1nQrA.pBbRu5kQQEqvdb71lFwH6G..RnlKxXS\",\"昵称\":\"奋斗在图书馆\",\"邮箱\":\"vbbb@qq.com\",\"地址\":\"黑龙江省哈尔滨市道里区\",\"用户唯一id\":\"0e338aad58384bc8b80c2de7f1bbd7da\",\"头像\":\"https://cdn.pixabay.com/photo/2017/08/06/12/06/people-2591874_960_720.jpg\",\"逻辑删除 0存在  id删除\":0,\"createTime\":1675431855000,\"updateTime\":1675431855000,\"角色\":\"USER\",\"score\":0},\"praiseCount\":3,\"collectCount\":1,\"commentCount\":1,\"followCount\":3,\"tags\":[\"24考研\",\"考研数学\"]}', '{\"code\":\"200\",\"msg\":\"操作成功\"}', 'http://localhost:9090/dynamic', 14, '110.248.181.2', '河北省邯郸市', 'admin', '2023-05-02 09:53:32', 0);
